import base64
import hashlib
import json


def CaiNiaoSign(request):
    hash_key = '06ho4hG556xO93s69b83Y051x7i194d3'#加盐字符串
    str2 =(json.dumps(request,ensure_ascii=False,separators=(',',':'))+hash_key)#转换成json对象去除，；号带空格问题
    md=hashlib.md5()#创建一个的方法
    md.update(str2.encode("utf-8"))
    #print(md.hexdigest())#得到的32字符十六进制数
    digest = base64.encodebytes(md.digest())#这里需要16字符二进制的
    return digest.decode()

